/*------------------------------------------------*/
/*	Master Slider: Get LIST of available Sliders  */
/*------------------------------------------------*/
function pix_get_master_sliders() {
	global $mspdb;


	$sliders = array( 0 => __('-- Select --', 'pix-opts') );

	// Revolution Slider ----------------------------------
	if( function_exists( 'masterslider' ) ){

		//$table_name = $wpdb->base_prefix . "revslider_sliders";
		//$array = $wpdb->get_results("SELECT * FROM $table_name ORDER BY title ASC");

		$master_sliders = $mspdb->get_sliders();
		if( is_array( $master_sliders ) ){
			foreach( $master_sliders as $v ){
				$sliders[$v['ID']] = $v['title'];
			}
		}
	}

	return $sliders;
}


/*-----------------------------------------------------------------------------------*/
/*	LIST of Sliders (Revolution Slider)
/*-----------------------------------------------------------------------------------*/
function pix_get_rev_sliders() {
	global $wpdb;

	$sliders = array( 0 => __('-- Select --', 'pix-opts') );

	// Revolution Slider ----------------------------------
	if( function_exists( 'rev_slider_shortcode' ) ){

		$table_name = $wpdb->base_prefix . "revslider_sliders";

		$array = $wpdb->get_results("SELECT * FROM $table_name ORDER BY title ASC");

		if( is_array( $array ) ){
			foreach( $array as $v ){
				$sliders[$v->alias] = $v->title;
			}
		}
	}

	return $sliders;
}


/*-----------------------------------------------------------------------------------*/
/*	LIST of Sliders (Layer Slider)
/*-----------------------------------------------------------------------------------*/
function pix_get_layer_sliders() {
	global $wpdb;

	$sliders = array( 0 => __('-- Select --', 'pix-opts') );

	// Layer Slider ----------------------------------
	if( function_exists( 'layerslider' ) ){

		$table_name = $wpdb->base_prefix . "layerslider";

		$array = $wpdb->get_results("SELECT * FROM $table_name WHERE flag_hidden = '0' AND flag_deleted = '0' ORDER BY name ASC");

		if( is_array( $array ) ){
			foreach( $array as $v ){
				$sliders[$v->id] = $v->name;
			}
		}
	}

	return $sliders;
}



/*-----------------------------------------------------------------------------------*/
/*	PRINT Single PixFort Options FIELD
/*-----------------------------------------------------------------------------------*/
function pix_meta_field_input( $field, $meta ){
	global $PIX_Options;


	if( isset( $field['type'] ) ){
		$fold = "";
		if(isset($field['fold'])){
			$fold="f_".$field['fold']." temphide";
		}
		echo '<tr valign="top" class="'.$fold.'">';

			// Field Title & SubDescription
			echo '<th scope="row">';
				if( key_exists('title', $field) ) echo $field['title'];
				if( key_exists('sub_desc', $field) ) echo '<span class="description">'. $field['sub_desc'] .'</span>';
			echo '</th>';

			// PixFort Options Field & Description

			echo '<td class="pix_meta_'.$field['type'].'">';
			switch ($field['type']) {
			    case "textarea":
			        echo '<textarea name="'. $field['id'] .'" class="large-text" rows="6" >'. $meta .'</textarea>';
			        echo (isset($field['desc']) && !empty($field['desc']))?'<br/><span class="description">'.$field['desc'].'</span>':'';
			        break;
			    case "text":
